export type RunOnlyType = 'rule' | 'rules' | 'tag' | 'tags';
export type TagValue = string;
export interface RunOnly {
    type: RunOnlyType;
    values: string[];
}
export interface RuleObject {
    [key: string]: {
        enabled: boolean;
    };
}
export interface RunOptions {
    runOnly?: RunOnly | TagValue[] | string[] | string;
    rules?: RuleObject;
    ancestry?: boolean;
}
